import React, { useEffect, useState } from 'react';
import axios from 'axios';
import ReviewForm from './components/ReviewForm';
import { useParams } from 'react-router-dom'; // Import useParams from react-router-dom

const BookDetailPage = () => {
  const [book, setBook] = useState(null);
  const { id } = useParams(); // Corrected to use useParams instead of match

  useEffect(() => {
    // Fetch the book details and reviews from the backend
    const fetchBook = async () => {
      try {
        const response = await axios.get(`/api/books/${id}`);
        setBook(response.data);
      } catch (error) {
        console.log('Error fetching book details:', error);
      }
    };

    fetchBook();
  }, [id]);

  if (!book) return <p>Loading...</p>;

  return (
    <div>
      <h1>{book.title}</h1>
      <p><strong>Author:</strong> {book.author}</p>
      <p><strong>Rating:</strong> {book.rating}</p>
      <p><strong>Description:</strong> {book.description}</p>

      <ReviewForm bookId={id} /> {/* Review form for this book */}

      <h2>Reviews</h2>
      {book.reviews.length === 0 ? (
        <p>No reviews yet.</p>
      ) : (
        <ul>
          {book.reviews.map((review) => (
            <li key={review._id}>
              <p><strong>{review.user.name}</strong> rated {review.rating} stars</p>
              <p>{review.comment}</p>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default BookDetailPage;
