import React, { useState, useEffect } from 'react';
import axios from '../../axiosConfig';
import { useNavigate, Link } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // This will only run once when the component mounts
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/'); // Redirect to homepage if the user is already logged in
    }
  }, [navigate]); // Empty dependency array means this effect will run only once when the component mounts

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('login', { email, password }); // Corrected URL

      if (response.data.success) {
        localStorage.setItem('token', response.data.token);
        navigate('/'); // Navigate to homepage upon successful login
      } else {
        setError(response.data.message || 'Invalid email or password');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setError('Failed to log in. Please check your credentials.');
    }
  };

  return (
    <div className="main">
      <div className="navbar">
        <div className="icon">
          <h2 className="logo">BookHub</h2>
        </div>
        <div className="menu">
          <ul>
            <li><Link to="/books">Books</Link></li>
            <li><Link to="/reviews">Reviews</Link></li>
            <li><Link to="/about">About</Link></li>
            <li><Link to="/contact">Contact</Link></li>
          </ul>
        </div>
      </div>
      <div className="content">
        <h1>Welcome to <span>BookHub</span></h1>
        <p>Discover your next favorite book and share your thoughts with our community.</p>

        <div className="form">
          <h2>Login</h2>
          <form onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Enter Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {error && <p className="error-message">{error}</p>} {/* Show error if any */}
            <button className="btnn" type="submit">Login</button>
          </form>
          <p className="link">
            Don't have an account? <Link to="/signup">Sign up</Link> now.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
