import React, { useEffect, useState } from 'react';
import axios from '../axiosConfig';

const RecommendedBook = () => {
  const [book, setBook] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchRecommendedBook = async () => {
      try {
        const response = await axios.get('/books/recommended');
        setBook(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching recommended book:', err);
        setError('Failed to load recommended book. Please try again later.');
        setLoading(false);
      }
    };

    fetchRecommendedBook();
  }, []);

  if (loading) {
    return <p>Loading...</p>;
  }

  if (error) {
    return <p>{error}</p>;
  }

  return (
    <div>
      <h1>Recommended Book</h1>
      {book ? (
        <div>
          <h2>{book.title}</h2>
          <p>{book.subtitle || 'No subtitle available'}</p>
          <p>{book.authors ? book.authors.join(', ') : 'No authors available'}</p>
          <p>{book.categories ? book.categories.join(', ') : 'No categories available'}</p>
          {book.thumbnail ? (
            <img src={book.thumbnail} alt={book.title} width="200" />
          ) : (
            <p>No thumbnail available</p>
          )}
          <p>{book.description || 'No description available'}</p>
          <p>Published Year: {book.published_year || 'N/A'}</p>
          <p>Average Rating: {book.average_rating || 'N/A'}</p>
          <p>Pages: {book.num_pages || 'N/A'}</p>
          <p>Ratings Count: {book.ratings_count || 'N/A'}</p>
        </div>
      ) : (
        <p>No recommended book available</p>
      )}
    </div>
  );
};

export default RecommendedBook;
