import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from '../../axiosConfig';
import './BookDetailsPage.css';

const BookDetailsPage = () => {
  const { id } = useParams();
  const [book, setBook] = useState(null);
  const [sameAuthorBooks, setSameAuthorBooks] = useState([]);
  const [sameCategoryBooks, setSameCategoryBooks] = useState([]);

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`/books/${id}`);
        setBook(response.data);

        const authorResponse = await axios.get(
          `/books/recommendations?author=${response.data.authors[0]}`
        );
        setSameAuthorBooks(authorResponse.data);

        const categoryResponse = await axios.get(
          `/books/recommendations?category=${response.data.categories[0]}`
        );
        setSameCategoryBooks(categoryResponse.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };

    fetchBookDetails();
  }, [id]);

  if (!book) return <p>Loading...</p>;

  return (
    <div className="book-details-page">
      <div className="book-details">
        <h1>{book.title}</h1>
        <p>{book.description || 'No description available'}</p>
        <p>Author: {book.authors.join(', ')}</p>
        <p>Categories: {book.categories.join(', ')}</p>
        <p>Average Rating: {book.average_rating}</p>
        <img src={book.thumbnail} alt={book.title} />
      </div>

      <div className="recommendations">
        <h2>More by {book.authors[0]}</h2>
        <div className="recommendation-grid">
          {sameAuthorBooks.map((recBook) => (
            <div key={recBook._id} className="book-card">
              <img src={recBook.thumbnail} alt={recBook.title} />
              <h3>{recBook.title}</h3>
              <p>{recBook.authors.join(', ')}</p>
            </div>
          ))}
        </div>

        <h2>Books in {book.categories[0]}</h2>
        <div className="recommendation-grid">
          {sameCategoryBooks.map((recBook) => (
            <div key={recBook._id} className="book-card">
              <img src={recBook.thumbnail} alt={recBook.title} />
              <h3>{recBook.title}</h3>
              <p>{recBook.authors.join(', ')}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default BookDetailsPage;
