import React, { useState } from 'react';
import './ReviewForm.css'; // Add a CSS file for consistent styling

const ReviewForm = () => {
  const [review, setReview] = useState('');
  const [message, setMessage] = useState('');

  const handleReviewSubmit = (e) => {
    e.preventDefault(); // Prevent page reload on form submit

    if (review.trim()) {
      // Handle review submission logic here (e.g., send to backend)
      console.log('Review Submitted:', review);

      // Optionally, clear the textarea after submitting
      setReview('');
      setMessage('Thank you for your review!');
    } else {
      setMessage('Please write a review before submitting.');
    }
  };

  return (
    <div className="review-form-container">
      <div className="review-form-card">
        <h2>Leave a Review</h2>
        <form onSubmit={handleReviewSubmit}>
          <textarea
            value={review}
            onChange={(e) => setReview(e.target.value)}
            placeholder="Write your review here..."
            rows="6"
            className="review-textarea"
          />
          <br />
          <button type="submit" className="review-btn">Submit Review</button>
        </form>
        {/* Feedback message */}
        {message && <p className={`message ${message.includes('Thank') ? 'success' : 'error'}`}>{message}</p>}
      </div>
    </div>
  );
};

export default ReviewForm;
