// HomePage.js
import React, { useEffect, useState } from 'react';
import axios from '../../axiosConfig'; // Use the axios instance
import './HomePage.css';

const HomePage = () => {
  const [books, setBooks] = useState([]);
  const [recommendedBook, setRecommendedBook] = useState(null); // Store recommended book
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  // Fetch all books and recommended book
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('/books');
        setBooks(response.data); // Assumes books are in response.data
      } catch (err) {
        console.error('Error fetching books:', err);
        setError('Failed to load books. Please try again later.');
      }
    };

    const fetchRecommendedBook = async () => {
      try {
        const response = await axios.get('/books/recommended');
        setRecommendedBook(response.data);
      } catch (err) {
        console.error('Error fetching recommended book:', err);
      }
    };

    fetchBooks();
    fetchRecommendedBook();
  }, []);

  // Handle search logic
  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setError('Please enter a valid search query');
      return;
    }
    setLoading(true);
    setError('');
    try {
      const response = await axios.get(`/books/search?query=${searchQuery}`);
      setSearchResults(response.data);
      if (response.data.length === 0) {
        setError('No results found.');
      }
    } catch (err) {
      console.error('Error searching books:', err);
      setError('An error occurred while searching.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="home-container">
      <h1>Welcome to BookHub</h1>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          value={searchQuery}
          onChange={(e) => setSearchQuery(e.target.value)}
          placeholder="Search books by title, author, or category"
          className="search-input"
        />
        <button onClick={handleSearch} className="search-btn">
          Search
        </button>
      </div>

      {/* Search Results */}
      {searchQuery && searchResults.length > 0 && (
        <div className="search-results">
          <h2>Search Results</h2>
          <ul>
            {searchResults.map((book) => (
              <li key={book.isbn13} className="book-item">
                <h3>{book.title}</h3>
                <p>{book.subtitle}</p>
                <p>{book.authors.join(', ')}</p>
                <p>{book.categories.join(', ')}</p>
              </li>
            ))}
          </ul>
        </div>
      )}

      {/* Recommended Book Section */}
      {recommendedBook && (
        <div className="recommended-section">
          <h2>Recommended Book</h2>
          <div className="recommended-book">
            <h3>{recommendedBook.title}</h3>
            <p>{recommendedBook.subtitle}</p>
            <p>{recommendedBook.authors.join(', ')}</p>
            <p>{recommendedBook.categories.join(', ')}</p>
            {recommendedBook.thumbnail && (
              <img
                src={recommendedBook.thumbnail}
                alt={recommendedBook.title}
                className="recommended-thumbnail"
              />
            )}
            <p>{recommendedBook.description}</p>
          </div>
        </div>
      )}

      {/* Books Grid */}
      <h2>Explore Our Collection</h2>
      {error && <p className="error-message">{error}</p>}
      <div className="books-grid">
        {books.map((book) => (
          <div className="book-card" key={book._id}>
            <img
              src={book.thumbnail}
              alt={book.title}
              className="book-thumbnail"
            />
            <h3>{book.title}</h3>
            <p>{book.authors.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
