import React, { useEffect, useState } from 'react';
import axios from '../axiosConfig';

const RecommendedBook = () => {
  const [book, setBook] = useState(null);

  useEffect(() => {
    const fetchRecommendedBook = async () => {
      try {
        const response = await axios.get('/books/recommended');
        setBook(response.data);
      } catch (err) {
        console.error('Error fetching recommended book:', err);
      }
    };

    fetchRecommendedBook();
  }, []);

  return (
    <div>
      <h1>Recommended Book</h1>
      {book ? (
        <div>
          <h2>{book.title}</h2>
          <p>{book.subtitle}</p>
          <p>{book.authors.join(', ')}</p>
          <p>{book.categories.join(', ')}</p>
          <img src={book.thumbnail} alt={book.title} />
          <p>{book.description}</p>
          <p>Published Year: {book.published_year}</p>
          <p>Average Rating: {book.average_rating}</p>
          <p>Pages: {book.num_pages}</p>
          <p>Ratings Count: {book.ratings_count}</p>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default RecommendedBook;
