import React, { useState } from 'react';
import axios from 'axios';

const ReviewForm = ({ bookId, onReviewSubmit }) => {
  const [rating, setRating] = useState(1);  // Default to 1 rating
  const [comment, setComment] = useState('');  // Initially empty comment

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post(`/api/reviews/add`, {
        bookId,
        rating,
        comment,
      });

      // On success, execute the callback to refresh the reviews list
      onReviewSubmit();
    } catch (error) {
      console.error('Failed to submit review:', error);
    }
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <label>
          Rating:
          <select
            value={rating}
            onChange={(e) => setRating(parseInt(e.target.value))}
          >
            {[1, 2, 3, 4, 5].map((star) => (
              <option key={star} value={star}>
                {star} Star{star > 1 ? 's' : ''}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Comment:
          <textarea
            value={comment}
            onChange={(e) => setComment(e.target.value)}
            required
          />
        </label>
        <br />
        <button type="submit">Submit Review</button>
      </form>
    </div>
  );
};

export default ReviewForm;
