import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from '../../axiosConfig'; // Use the axios instance
import './HomePage.css';

const HomePage = () => {
  const [books, setBooks] = useState([]);
  const [query, setQuery] = useState('');
  const navigate = useNavigate();

  // Fetch books from the backend
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('/books');
        setBooks(response.data); // Assumes books are in response.data
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  const handleSearch = () => {
    if (query.trim()) {
      navigate(`/search?query=${query}`);
    }
  };

  return (
    <div className="home-container">
      <h1>Welcome to BookHub</h1>
      <p>Explore our collection of books!</p>

      <div className="search-bar">
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search for books by title, author, or category"
        />
        <button onClick={handleSearch}>Search</button>
      </div>

      <div className="books-grid">
        {books.map((book) => (
          <div className="book-card" key={book._id}>
            <img
              src={book.thumbnail}
              alt={book.title}
              className="book-thumbnail"
              onClick={() => navigate(`/book/${book._id}`)} // Redirect to BookDetailsPage
            />
            <h3>{book.title}</h3>
            <p>{book.authors.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
