import React, { useState } from 'react';
import axios from '../../axiosConfig';
import './SearchBooks.css'; // Importing the CSS for consistent styling

const SearchBooks = () => {
  const [query, setQuery] = useState('');
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');

  const handleSearch = async () => {
    if (!query.trim()) {
      setMessage('Please enter a search query');
      return;
    }

    try {
      const response = await axios.get(`/books/search?query=${query}`);
      if (response.data.length === 0) {
        setMessage('No books found');
      } else {
        setMessage('');
        setResults(response.data);
      }
    } catch (err) {
      console.error('Error searching books:', err);
      setMessage('An error occurred while searching for books');
    }
  };

  return (
    <div className="search-books-container">
      <div className="search-books-card">
        <h2>Search Books</h2>
        <input
          type="text"
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          placeholder="Search by title, subtitle, author, or category"
          className="search-input"
        />
        <button onClick={handleSearch} className="search-btn">Search</button>

        {message && <p className={`message ${message.includes('No') ? 'error' : 'info'}`}>{message}</p>}

        <div className="search-results">
          {results.length > 0 && (
            <ul>
              {results.map((book) => (
                <li key={book.isbn13} className="book-item">
                  <h3>{book.title}</h3>
                  <p>{book.subtitle}</p>
                  <p>{book.authors.join(', ')}</p>
                  <p>{book.categories.join(', ')}</p>
                </li>
              ))}
            </ul>
          )}
        </div>
      </div>
    </div>
  );
};

export default SearchBooks;
