import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
  const [books, setBooks] = useState([]);

  // Fetch books from the backend
  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/books');
        setBooks(response.data); // Assumes books are in response.data
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  return (
    <div className="home-container">
      <h1>Welcome to BookHub</h1>
      <p>Explore our collection of books!</p>
      <div className="books-grid">
        {books.map((book) => (
          <div className="book-card" key={book._id}>
            <img
              src={book.thumbnail} // Changed to 'book.thumbnail' from 'book.thumbnailUrl'
              alt={book.title}
              className="book-thumbnail"
            />
            <h3>{book.title}</h3>
            <p>{book.authors.join(', ')}</p> {/* Joining authors array into a string */}
          </div>
        ))}
      </div>
    </div>
  );
};

export default HomePage;
