import React, { useState, useEffect } from 'react';
import { useSearchParams, useNavigate } from 'react-router-dom';
import axios from '../../axiosConfig';
import './SearchResultsPage.css';

const SearchResultsPage = () => {
  const [searchParams] = useSearchParams();
  const [results, setResults] = useState([]);
  const [message, setMessage] = useState('');
  const query = searchParams.get('query');
  const navigate = useNavigate();

  useEffect(() => {
    const fetchSearchResults = async () => {
      try {
        const response = await axios.get(`/books/search?query=${query}`);  // Correct endpoint
        if (response.data.length === 0) {
          setMessage('No books found');
        } else {
          setMessage('');
          setResults(response.data);
        }
      } catch (err) {
        if (err.response && err.response.status === 400) {
          setMessage('Invalid search query. Please try again.');
        } else if (err.response && err.response.status === 404) {
          setMessage('No books found matching your search.');
        } else {
          setMessage('An error occurred while searching for books.');
        }
        console.error('Error searching books:', err);
      }
    };

    if (query) fetchSearchResults();
  }, [query]);

  return (
    <div className="search-results-page">
      <h2>Search Results for "{query}"</h2>
      {message && <p className="message">{message}</p>}
      <div className="results-grid">
        {results.map((book) => (
          <div
            key={book._id}
            className="book-card"
            onClick={() => navigate(`/book/${book._id}`)}  // Navigate to book details
          >
            <img src={book.thumbnail} alt={book.title} className="book-thumbnail" />
            <h3>{book.title}</h3>
            <p>{book.authors.join(', ')}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default SearchResultsPage;
