import React from 'react';
import BookList from './components/BookList/BookList'; 
import SearchBooks from './components/SearchBooks/SearchBooks'; 
import RecommendedBook from './components/RecommendedBook'; 
import ReviewForm from './components/ReviewForm/ReviewForm'; 
import LoginPage from './pages/LoginPage/LoginPage';
import HomePage from './pages/HomePage/HomePage'; // Add the HomePage component
import ProtectedRoute from './components/ProtectedRoute';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom'; // Add routing support

const App = () => {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/login" element={<LoginPage />} />
        <Route path="/books" element={<BookList />} />
        <Route path="/search" element={<SearchBooks />} />
        <Route path="/recommended" element={<RecommendedBook />} />
        <Route path="/review" element={<ReviewForm />} />
         <Route
          path="/"
          element={
            <ProtectedRoute>
              <HomePage />
            </ProtectedRoute>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
