import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from '../../axiosConfig';
import './BookDetailsPage.css';

const BookDetailsPage = () => {
  const { bookId } = useParams(); // Extract bookId from the URL
  const navigate = useNavigate();
  const [book, setBook] = useState(null);
  const [rating, setRating] = useState('');
  const [review, setReview] = useState('');
  const [recommendedBooks, setRecommendedBooks] = useState([]);
  const [message, setMessage] = useState('');

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`/books/${bookId}`); // Fetch book details by bookId
        setBook(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };

    const fetchRecommendedBooks = async () => {
      try {
        const response = await axios.get(`/books/recommended/${bookId}`); // Fetch recommended books based on bookId
        setRecommendedBooks(response.data);
      } catch (error) {
        console.error('Error fetching recommended books:', error);
      }
    };

    fetchBookDetails();
    fetchRecommendedBooks();
  }, [bookId]); // Run the effect when bookId changes

  const handleReviewSubmit = async () => {
    if (!review || !rating) {
      setMessage('Please provide both a rating and a review.');
      return;
    }

    try {
      await axios.post(`/reviews`, {
        bookId,
        rating,
        review
      });
      setMessage('Review submitted successfully!');
    } catch (error) {
      setMessage('Error submitting review.');
    }
  };

  const generateStars = (rating) => {
    const fullStars = Math.floor(rating);
    const halfStars = rating % 1 >= 0.5 ? 1 : 0;
    const emptyStars = 5 - fullStars - halfStars;
    
    return (
      <div className="stars">
        {'★'.repeat(fullStars)}
        {halfStars === 1 ? '☆' : ''}
        {'☆'.repeat(emptyStars)}
      </div>
    );
  };

  return (
    <div className="book-details-page">
      {book ? (
        <>
          <div className="book-header">
            <img src={book.thumbnail} alt={book.title} className="book-thumbnail" />
            <div className="book-info">
              <h1>{book.title}</h1>
              <h2>{book.authors.join(', ')}</h2>
              <p>{book.description}</p>
              <p><strong>Published Year:</strong> {book.published_year}</p>
              <p><strong>Pages:</strong> {book.num_pages}</p>

              <div className="book-rating">
                <strong>Average Rating: </strong>
                {generateStars(book.average_rating)}
              </div>
            </div>
          </div>

          <div className="review-section">
            <h3>Leave a Review</h3>
            <div className="review-form">
              <label>Rating (out of 5):</label>
              <input
                type="number"
                min="1"
                max="5"
                value={rating}
                onChange={(e) => setRating(e.target.value)}
              />

              <label>Review:</label>
              <textarea
                value={review}
                onChange={(e) => setReview(e.target.value)}
                rows="5"
                placeholder="Write your review here..."
              />

              <button onClick={handleReviewSubmit}>Submit Review</button>
            </div>
            {message && <p className="message">{message}</p>}
          </div>

          <div className="recommended-books">
            <h3>Recommended Books</h3>
            <div className="books-grid">
              {recommendedBooks.map((recommendedBook) => (
                <div className="book-card" key={recommendedBook._id}>
                  <img
                    src={recommendedBook.thumbnail}
                    alt={recommendedBook.title}
                    className="book-thumbnail"
                    onClick={() => navigate(`/book/${recommendedBook._id}`)} // Redirect to recommended book details
                  />
                  <h3>{recommendedBook.title}</h3>
                  <p>{recommendedBook.authors.join(', ')}</p>
                  <div className="book-rating">
                    {generateStars(recommendedBook.average_rating)}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default BookDetailsPage;
