const BookDetailsPage = () => {
  const { bookId } = useParams();
  const [bookDetails, setBookDetails] = useState(null);
  const [recommendedBooks, setRecommendedBooks] = useState([]);
  const [reviews, setReviews] = useState([]);

  // Function to fetch the book details, reviews and recommended books
  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`/api/books/${bookId}`);
        setBookDetails(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };

    const fetchRecommendedBooks = async () => {
      try {
        const response = await axios.get(`/api/books/recommended/${bookId}`);
        setRecommendedBooks(response.data);
      } catch (error) {
        console.error('Error fetching recommended books:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await axios.get(`/api/reviews/${bookId}`);
        setReviews(response.data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    if (bookId) {
      fetchBookDetails();
      fetchRecommendedBooks();
      fetchReviews();
    }
  }, [bookId]);

  // Callback to refresh reviews and update state after a new one is added
  const handleNewReview = (newReview) => {
    // Add the new review to the current reviews list
    setReviews((prevReviews) => [...prevReviews, newReview]);
  };

  return (
    <div>
      {bookDetails ? (
        <div>
          <h1>{bookDetails.title}</h1>
          <p><strong>Authors:</strong> {bookDetails.authors.join(', ')}</p>
          <p><strong>Categories:</strong> {bookDetails.categories.join(', ')}</p>
          <p><strong>Description:</strong> {bookDetails.description}</p>
          <p><strong>Published Year:</strong> {bookDetails.published_year}</p>
          <p><strong>Number of Pages:</strong> {bookDetails.num_pages}</p>
        </div>
      ) : (
        <p>Loading book details...</p>
      )}

      <h2>Reviews</h2>
      {reviews.length > 0 ? (
        reviews.map((review) => (
          <div key={review._id} className="review-card">
            <p>{review.comment}</p>
            <p>Rating: {review.rating} stars</p>
          </div>
        ))
      ) : (
        <p>No reviews yet for this book. Be the first to review!</p>
      )}

      {/* Review form that will call the handleNewReview callback */}
      <ReviewForm bookId={bookId} onReviewSubmit={handleNewReview} />

      <h2>Recommended Books</h2>
      <div style={{ display: 'flex', gap: '20px', flexWrap: 'wrap' }}>
        {recommendedBooks.length > 0 ? (
          recommendedBooks.map((book) => (
            <div
              key={book._id}
              style={{
                border: '1px solid #ddd',
                borderRadius: '10px',
                padding: '10px',
                textAlign: 'center',
                boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
                width: '150px',
              }}
            >
              <img
                src={book.thumbnail}
                alt={book.title}
                style={{
                  width: '100%',
                  height: 'auto',
                  borderRadius: '5px',
                  marginBottom: '10px',
                }}
              />
              <h3 style={{ fontSize: '1rem', margin: '0 0 5px' }}>{book.title}</h3>
              <p style={{ fontSize: '0.8rem', color: '#777' }}>
                {book.authors.join(', ')}
              </p>
            </div>
          ))
        ) : (
          <p>No recommended books available.</p>
        )}
      </div>
    </div>
  );
};

export default BookDetailsPage;
