import React, { useState, useEffect } from 'react';
import axiosInstance from '../../axiosConfig';  // The axiosInstance import seems fine

const SearchResultsPage = () => {
  const [searchQuery, setSearchQuery] = useState('');  // Query state for the search
  const [books, setBooks] = useState([]);              // To store the results
  const [error, setError] = useState('');               // To handle errors

  // Handle searching for books
  const handleSearch = async () => {
    if (!searchQuery.trim()) {
      setError('Please enter a search query');
      setBooks([]);  // Clear any previous results
      return;
    }

    try {
      const response = await axiosInstance.get('/books/search', {   // Update path to call the search endpoint
        params: { query: searchQuery }  // Pass query as param correctly
      });
      setBooks(response.data);
      setError('');  // Clear any existing errors
    } catch (err) {
      setError('Error while fetching books. Please try again later.');
    }
  };

  useEffect(() => {
    if (searchQuery) {
      handleSearch();  // Trigger search when the query changes
    }
  }, [searchQuery]);

  return (
    <div>
      <input
        type="text"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}  // Update state on input change
        placeholder="Search by title or author"
      />
      {error && <div>{error}</div>}
      <div>
        {books.length > 0 ? (
          books.map((book) => (
            <div key={book._id}>  
              <h3>{book.title}</h3>
              <p>{book.authors.join(', ')}</p>
            </div>
          ))
        ) : (
          <p>No books found</p>
        )}
      </div>
    </div>
  );
};

export default SearchResultsPage;
