import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Import useParams
import axios from 'axios';
import './BookDetailsPage.css'; // Custom CSS for styling

const BookDetailsPage = () => {
  const { bookId } = useParams(); // Access bookId from the route parameters
  const [bookDetails, setBookDetails] = useState(null);
  const [recommendedBooks, setRecommendedBooks] = useState([]);
  const [reviews, setReviews] = useState([]);
  const [newReview, setNewReview] = useState({ rating: 1, comment: '' });
  const [errorMessage, setErrorMessage] = useState('');

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`/api/books/${bookId}`);
        setBookDetails(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };

    const fetchRecommendedBooks = async () => {
      try {
        const response = await axios.get(`/api/books/recommended/${bookId}`);
        setRecommendedBooks(response.data);
      } catch (error) {
        console.error('Error fetching recommended books:', error);
      }
    };

    const fetchReviews = async () => {
      try {
        const response = await axios.get(`/api/reviews/${bookId}`);
        setReviews(response.data);
      } catch (error) {
        console.error('Error fetching reviews:', error);
      }
    };

    if (bookId) {
      fetchBookDetails();
      fetchRecommendedBooks();
      fetchReviews();
    }
  }, [bookId]);

  const handleSubmitReview = async (e) => {
    e.preventDefault();
    const userId = 'user123'; // Get this dynamically from user auth in a real app
    try {
      const response = await axios.post('/api/reviews/add', { ...newReview, bookId, userId });
      setReviews([response.data.review, ...reviews]); // Add new review to the list
      setNewReview({ rating: 1, comment: '' }); // Reset form fields
    } catch (error) {
      setErrorMessage('Failed to submit review, please try again.');
    }
  };

  return (
    <div className="book-details-container">
      {bookDetails ? (
        <>
          <div className="hero-section">
            <h1>{bookDetails.title}</h1>
            <p>{bookDetails.description}</p>
          </div>

          <h2>Write a Review</h2>
          {errorMessage && <p className="error-message">{errorMessage}</p>}

          <form onSubmit={handleSubmitReview} className="review-form">
            <div className="rating-container">
              <label htmlFor="rating">Rating:</label>
              <select
                id="rating"
                value={newReview.rating}
                onChange={(e) => setNewReview({ ...newReview, rating: Number(e.target.value) })}
                className="rating-select"
              >
                {[1, 2, 3, 4, 5].map((star) => (
                  <option key={star} value={star}>
                    {star} Star{star > 1 && 's'}
                  </option>
                ))}
              </select>
            </div>

            <div className="comment-container">
              <label htmlFor="comment">Comment:</label>
              <textarea
                id="comment"
                value={newReview.comment}
                onChange={(e) => setNewReview({ ...newReview, comment: e.target.value })}
                placeholder="Write your review here"
                required
              />
            </div>

            <button type="submit" className="submit-review-btn">
              Submit Review
            </button>
          </form>

          <div className="reviews-section">
            <h2>Reviews</h2>
            {reviews.length > 0 ? (
              <div className="reviews-list">
                {reviews.map((review) => (
                  <div key={review._id} className="review-card">
                    <p className="review-rating">Rating: {review.rating} Stars</p>
                    <p className="review-comment">{review.comment}</p>
                  </div>
                ))}
              </div>
            ) : (
              <p>No reviews yet for this book.</p>
            )}
          </div>

          <h2>Recommended Books</h2>
          <div className="recommended-books">
            {recommendedBooks.map((book) => (
              <div className="recommended-book" key={book._id}>
                <img src={book.thumbnail} alt={book.title} />
                <h3>{book.title}</h3>
                <p>{book.authors.join(', ')}</p>
              </div>
            ))}
          </div>
        </>
      ) : (
        <p>Loading book details...</p>
      )}
    </div>
  );
};

export default BookDetailsPage;
