import React from 'react';
import './LoginPage.css';

const LoginPage = () => {
  const handleLogin = (e) => {
    e.preventDefault();
    const email = document.querySelector('input[type="email"]').value;
    const password = document.querySelector('input[type="password"]').value;

    console.log(`Email: ${email}, Password: ${password}`);
    // Integrate with backend API here
  };

  return (
    <div className="main">
      <div className="navbar">
        <div className="icon">
          <h2 className="logo">BookHub</h2>
        </div>
        <div className="menu">
          <ul>
            <li><a href="/books">Books</a></li>
            <li><a href="/reviews">Reviews</a></li>
            <li><a href="/about">About</a></li>
            <li><a href="/contact">Contact</a></li>
          </ul>
        </div>
      </div>
      <div className="content">
        <h1>Welcome to <span>BookHub</span></h1>
        <p className="par">Discover your next favorite book and share your thoughts with our community.</p>

        <div className="form">
          <h2>Login</h2>
          <form onSubmit={handleLogin}>
            <input type="email" name="email" placeholder="Enter Email" required />
            <input type="password" name="password" placeholder="Enter Password" required />
            <button className="btnn" type="submit">Login</button>
          </form>
          <p className="link">
            Don't have an account? <a href="/signup">Sign up</a> now.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
