import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom'; // Import useParams and useNavigate
import axios from '../../axiosConfig'; // Use axios instance
import './BookDetailsPage.css'; // Import the CSS file

const BookDetailsPage = () => {
  const { bookId } = useParams(); // Access bookId from the route parameters
  const [bookDetails, setBookDetails] = useState(null);
  const [recommendedBooks, setRecommendedBooks] = useState([]);
  const navigate = useNavigate(); // For navigating to recommended book details

  useEffect(() => {
    const fetchBookDetails = async () => {
      try {
        const response = await axios.get(`/api/books/${bookId}`);
        setBookDetails(response.data);
      } catch (error) {
        console.error('Error fetching book details:', error);
      }
    };

    const fetchRecommendedBooks = async () => {
      try {
        const response = await axios.get(`/api/books/recommended/${bookId}`);
        setRecommendedBooks(response.data);
      } catch (error) {
        console.error('Error fetching recommended books:', error);
      }
    };

    if (bookId) {
      fetchBookDetails();
      fetchRecommendedBooks();
    }
  }, [bookId]);

  return (
    <div className="details-container">
      {bookDetails ? (
        <div className="book-details">
          <img
            src={bookDetails.thumbnail}
            alt={bookDetails.title}
            className="book-thumbnail"
          />
          <div className="book-info">
            <h1 className="book-title">{bookDetails.title}</h1>
            <p className="book-authors">By {bookDetails.authors.join(', ')}</p>
            <p className="book-description">{bookDetails.description}</p>
            <p className="book-category">Category: {bookDetails.category}</p>
            <p className="book-rating">Average Rating: {bookDetails.average_rating}</p>
          </div>
        </div>
      ) : (
        <p className="loading">Loading book details...</p>
      )}

      <h2 className="recommendations-header">Recommended Books</h2>
      <div className="recommended-books-grid">
        {recommendedBooks.length > 0 ? (
          recommendedBooks.map((book) => (
            <div
              className="recommended-book-card"
              key={book._id}
              onClick={() => navigate(`/book/${book._id}`)} // Navigate to the recommended book
            >
              <img
                src={book.thumbnail}
                alt={book.title}
                className="recommended-thumbnail"
              />
              <h3 className="recommended-title">{book.title}</h3>
              <p className="recommended-authors">
                By {book.authors.join(', ')}
              </p>
            </div>
          ))
        ) : (
          <p className="no-recommendations">No recommended books available.</p>
        )}
      </div>
    </div>
  );
};

export default BookDetailsPage;
