import React, { useState, useEffect } from 'react';
import axios from '../../axiosConfig';
import { useNavigate } from 'react-router-dom';
import './LoginPage.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  // Redirect to homepage if already logged in
  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      navigate('/'); // Redirect to homepage if token exists
    }
  }, [navigate]);

  const handleLogin = async (e) => {
    e.preventDefault();

    try {
      const response = await axios.post('/users/login', { email, password });

      if (response.data.success) {
        console.log('Login successful');
        // Save JWT token to localStorage
        localStorage.setItem('token', response.data.token);
        // Navigate to the home page
        navigate('/');
      } else {
        setError(response.data.message || 'Invalid email or password');
      }
    } catch (error) {
      console.error('Error logging in:', error);
      setError('Failed to log in. Please check your credentials.');
    }
  };

  return (
    <div className="main">
      <div className="navbar">
        <div className="icon">
          <h2 className="logo">BookHub</h2>
        </div>
        <div className="menu">
          <ul>
            <li><a href="/books">Books</a></li>
            <li><a href="/reviews">Reviews</a></li>
            <li><a href="/about">About</a></li>
            <li><a href="/contact">Contact</a></li>
          </ul>
        </div>
      </div>
      <div className="content">
        <h1>Welcome to <span>BookHub</span></h1>
        <p className="par">Discover your next favorite book and share your thoughts with our community.</p>

        <div className="form">
          <h2>Login</h2>
          <form onSubmit={handleLogin}>
            <input
              type="email"
              placeholder="Enter Email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              required
            />
            <input
              type="password"
              placeholder="Enter Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            {error && <p className="error-message">{error}</p>}
            <button className="btnn" type="submit">Login</button>
          </form>
          <p className="link">
            Don't have an account? <a href="/signup">Sign up</a> now.
          </p>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
